import React, { useEffect, useState } from 'react';
import { fetchData, deleteData, updateData } from '../api'; // Zakładamy, że masz odpowiednie funkcje API
import { Link } from 'react-router-dom';

const Licznik = ({ isAdmin }) => {
    const [data, setData] = useState([]);

    useEffect(() => {
        const getData = async () => {
            const endpoint = 'liczniki/';
            try {
                const result = await fetchData(endpoint);
                console.log("Dane załadowane:", result); // Logowanie danych
                if (isAdmin) {
                    setData(result); // Admin widzi wszystkie
                } else {
                    setData(result); // Zwykły użytkownik widzi swoje (jeśli backend zwróci odpowiednie dane)
                }
            } catch (error) {
                console.error("Błąd pobierania danych:", error);
            }
        };

        getData();
    }, [isAdmin]);

    const handleDelete = async (id) => {
        console.log(`Usuwanie licznika o ID: ${id}`); // Logowanie ID licznika
        const endpoint = `liczniki/${id}`;
        try {
            await deleteData(endpoint);
            setData(data.filter(item => item.id !== id)); // Usuwanie z widoku
        } catch (error) {
            console.error("Błąd podczas usuwania:", error);
        }
    };

    const handleUpdate = async (id, updatedData) => {
        console.log(`Edycja licznika o ID: ${id} z danymi:`, updatedData); // Logowanie danych do edycji
        const endpoint = `liczniki/${id}`;
        try {
            await updateData(endpoint, updatedData);
            setData(data.map(item => (item.id === id ? { ...item, ...updatedData } : item))); // Aktualizacja stanu
        } catch (error) {
            console.error("Błąd podczas edytowania:", error);
        }
    };

    return (
        <div className="container">
            <h1>Liczniki</h1>
            {data.length > 0 ? (
                data.map((item) => (
                    <div key={item.id} className="data-block">
                        <p><strong>ID:</strong> {item.id}</p>
                        <p><strong>Typ:</strong> {item.typ_licznika}</p>
                        <p><strong>Wartość:</strong> {item.odczyt}</p>
                        <p><strong>Data Odczytu:</strong> {item.data_odczytu}</p>

                        {isAdmin && (
                            <div>
                                <button onClick={() => handleDelete(item.id)}>Usuń</button>
                                <button onClick={() => handleUpdate(item.id, { typ_licznika: 'Nowy Typ' })}>Edytuj</button>
                            </div>
                        )}
                    </div>
                ))
            ) : (
                <p>Brak danych do wyświetlenia</p>
            )}
            <Link to="/">Powrót do Home</Link>
        </div>
    );
};

export default Licznik;
