import React, { useEffect, useState } from 'react';
import { fetchData} from '../api'; // Funkcje API
import { Link } from 'react-router-dom';
import './Rozliczenie.css'

const Rozliczenia = ({ isAdmin }) => {
    const [data, setData] = useState([]);
    const [users, setUsers] = useState([]); // Lista użytkowników

    useEffect(() => {
        const getData = async () => {
            const endpoint = 'rozliczenia/';
            const result = await fetchData(endpoint);
            setData(result);
        };

        const getUsers = async () => {
            const usersEndpoint = 'users/';
            const result = await fetchData(usersEndpoint);
            setUsers(result);
        };

        getData();
        getUsers();
    }, []);

    return (
        <div className="container">
            <h1>Rozliczenia</h1>
            <table>
                <thead>
                    <tr>
                        <th>Kwota</th>
                        <th>Data Rozliczenia</th>
                        <th>Mieszkaniec</th>
                        <th>Status</th>
                        {isAdmin && <th>Akcje</th>}
                    </tr>
                </thead>
                <tbody>
                    {data.length > 0 ? (
                        data.map((item) => {
                            const user = users.find((user) => user.id === item.mieszkaniec);
                            return (
                                <tr key={item.id}>
                                    <td>{item.kwota}</td>
                                    <td>{item.data_rozliczenia}</td>
                                    <td>{user ? `${user.first_name} ${user.last_name}` : 'Brak danych'}</td>
                                    <td>{item.status}</td>
                                    {isAdmin && (
                                        <td>
                                            <Link to={`/rozliczenie/details/${item.id}`}>
                                                <button>Szczegóły</button>
                                            </Link>
                                            <Link to={`/rozliczenie/edit/${item.id}`}>
                                                <button>Edytuj</button>
                                            </Link>
                                        </td>
                                    )}
                                </tr>
                            );
                        })
                    ) : (
                        <tr>
                            <td colSpan="5">Brak danych do wyświetlenia</td>
                        </tr>
                    )}
                </tbody>
            </table>
            {isAdmin && (
                <Link to="/rozliczenie/add">
                    <button>Dodaj Nowe Rozliczenie</button>
                </Link>
            )}
        </div>
    );
};

export default Rozliczenia;
