import React, { useEffect, useState } from 'react';
import { fetchData, deleteData, updateData } from '../api'; // Załóżmy, że masz odpowiednie funkcje API
import { Link } from 'react-router-dom';

const Licznik = ({ isAdmin }) => {
    const [data, setData] = useState([]);

    useEffect(() => {
        const getData = async () => {
            const endpoint = 'liczniki/';
            const result = await fetchData(endpoint);

            // Jeśli użytkownik jest administratorem, pobieramy wszystkie liczniki
            if (isAdmin) {
                setData(result);
            } else {
                // Jeśli to zwykły użytkownik, nie musimy robić filtracji po user_id
                // Zakładamy, że backend już filtruje liczniki przypisane do użytkownika
                setData(result); // W przypadku zwykłego użytkownika, zwrócone dane będą dotyczyły tylko jego liczników
            }
        };

        getData();
    }, [isAdmin]);

    const handleDelete = async (id) => {
        const endpoint = `liczniki/${id}`;
        await deleteData(endpoint);
        setData(data.filter(item => item.id !== id)); // Usuwamy z widoku
    };

    const handleUpdate = async (id, updatedData) => {
        const endpoint = `liczniki/${id}`;
        await updateData(endpoint, updatedData);
        setData(data.map(item => (item.id === id ? { ...item, ...updatedData } : item))); // Aktualizujemy stan
    };

    return (
        <div className="container">
            <h1>Liczniki</h1>
            {data.length > 0 ? (
                data.map((item) => (
                    <div key={item.id} className="data-block">
                        <p><strong>ID:</strong> {item.id}</p>
                        <p><strong>Typ:</strong> {item.typ_licznika}</p>
                        <p><strong>Wartość:</strong> {item.odczyt}</p>
                        <p><strong>Data Odczytu:</strong> {item.data_odczytu}</p>

                        {isAdmin && (
                            <div>
                                <button onClick={() => handleDelete(item.id)}>Usuń</button>
                                <button onClick={() => handleUpdate(item.id, { typ_licznika: 'Nowy Typ' })}>Edytuj</button>
                            </div>
                        )}
                    </div>
                ))
            ) : (
                <p>Brak danych do wyświetlenia</p>
            )}
            <Link to="/">Powrót do Home</Link>
        </div>
    );
};

export default Licznik;
