import React, { useEffect, useState } from 'react';
import { fetchData, postData, updateData, deleteData } from '../api';
import { Link, useParams } from 'react-router-dom';
import './Common.css'; // Import the common CSS file

const Usterka = ({ isAdmin }) => {
    const { id } = useParams(); // Dla edycji/wyświetlania szczegółów usterki
    const [data, setData] = useState([]);
    const [newUsterka, setNewUsterka] = useState('');
    const [usterka, setUsterka] = useState(null);
    const [newStatus, setNewStatus] = useState('');

    useEffect(() => {
        const getData = async () => {
            if (id) {
                const result = await fetchData(`usterki/admin/${id}/`);
                setUsterka(result);
                setNewStatus(result.status); // Zakładając, że usterka ma status
            } else {
                const result = await fetchData('usterki/');
                setData(result);
            }
        };
        getData();
    }, [id, isAdmin]);

    const handleAddUsterka = async () => {
        if (isAdmin) {
            alert('Admin nie może dodać nowej usterki');
            return;
        }
        try {
            console.log('Dodawanie usterki:', { opis: newUsterka });
            const result = await postData('usterki/', { opis: newUsterka });
            setData([...data, result]);
            setNewUsterka('');
        } catch (error) {
            console.error('Błąd podczas dodawania usterki:', error);
            alert('Nie udało się dodać usterki');
        }
    };

    const handleUpdateUsterka = async () => {
        if (!isAdmin) {
            alert('Tylko admin może zmieniać status usterki');
            return;
        }
        try {
            const result = await updateData(`usterki/admin/${id}/`, { status: newStatus });
            setUsterka(result);
            alert('Status usterki został zaktualizowany');
        } catch (error) {
            console.error('Błąd podczas aktualizowania usterki:', error);
            alert('Nie udało się zaktualizować usterki');
        }
    };

    const handleDeleteUsterka = async (id) => {
        if (!isAdmin) {
            alert('Tylko admin może usunąć usterkę');
            return;
        }
        try {
            await deleteData(`usterki/admin/${id}/`);
            setData(data.filter(item => item.id !== id)); // Usuwanie z widoku
            alert('Usterka została usunięta');
        } catch (error) {
            console.error('Błąd podczas usuwania usterki:', error);
            alert('Nie udało się usunąć usterki');
        }
    };

    const handleStatusChange = (id, status) => {
        // Zmieniamy status w lokalnym stanie dla danego elementu
        setData(data.map(item => item.id === id ? { ...item, status } : item));
    };

    const handleUpdateStatus = async (id, status) => {
        try {
            const result = await updateData(`usterki/admin/${id}/`, { status });
            setData(data.map(item => item.id === id ? result : item));
            alert('Status usterki został zaktualizowany');
        } catch (error) {
            console.error('Błąd podczas aktualizowania statusu:', error);
            alert('Nie udało się zaktualizować statusu');
        }
    };

    if (id && usterka) {
        return (
            <div className="container">
                <h1>Usterka</h1>
                <p>{usterka.opis}</p>
                <p><strong>Status:</strong> {usterka.status}</p>
                <select
                    value={newStatus}
                    onChange={(e) => setNewStatus(e.target.value)}
                >
                    <option value="nowa">Nowa</option>
                    <option value="w trakcie">W trakcie</option>
                    <option value="naprawiona">Naprawiona</option>
                </select>
                <button onClick={handleUpdateUsterka} className='btn btn-success'>Zaktualizuj status</button>
                
                {isAdmin && (
                    <button onClick={() => handleDeleteUsterka(usterka.id)} className='btn btn-danger'>Usuń usterkę</button>
                )}

                <Link to="/" className="button">Powrót do Home</Link>
            </div>
        );
    }

    return (
        <div className="container">
            <h1>Usterki</h1>
            {data.map((item) => (
                <div key={item.id} className="usterka-block">
                    <p><strong>ID:</strong> {item.id}</p>
                    <p><strong>Mieszkaniec:</strong> {item.mieszkaniec}</p>
                    <p><strong>Opis:</strong> {item.opis}</p>
                    <p><strong>Status:</strong> {item.status}</p>
                    {isAdmin && (
                        <div>
                            <select
                                value={item.status}
                                onChange={(e) => handleStatusChange(item.id, e.target.value)}
                            >
                                <option value="nowa">Nowa</option>
                                <option value="w trakcie">W trakcie</option>
                                <option value="naprawiona">Naprawiona</option>
                            </select>
                            <button onClick={() => handleUpdateStatus(item.id, item.status)} className='btn btn-success button-spacing2'>Zaktualizuj status</button>
                            <button onClick={() => handleDeleteUsterka(item.id)} className='btn btn-danger'>Usuń usterkę</button>
                        </div>
                    )}
                </div>
            ))}
            <Link to="/" className="button">Powrót do Home</Link>
            {!isAdmin && (
                <div>
                    <input
                        type="text"
                        value={newUsterka}
                        onChange={(e) => setNewUsterka(e.target.value)}
                        placeholder="Dodaj nową usterkę"
                    />
                    <button onClick={handleAddUsterka}>Dodaj</button>
                </div>
            )}
        </div>
    );
};

export default Usterka;
