import React, { useEffect, useState } from 'react';
import { fetchData, postData, updateData } from '../api';
import { Link } from 'react-router-dom';

const Usterka = ({ isAdmin }) => {
    const [data, setData] = useState([]);
    const [newUsterka, setNewUsterka] = useState('');

    useEffect(() => {
        const getData = async () => {
            const endpoint = 'usterki/';
            const result = await fetchData(endpoint);
            setData(result);
        };
        getData();
    }, [isAdmin]);

    const handleAddUsterka = async () => {
        try {
            const endpoint = 'usterki/';
            const result = await postData(endpoint, { opis: newUsterka });
            setData([...data, result]);
            setNewUsterka('');
        } catch (error) {
            console.error('Error adding usterka:', error);
            alert('Failed to add usterka');
        }
    };

    const handleUpdateUsterka = async (id, status) => {
        if (!isAdmin) {
            alert('Only admins can change the status');
            return;
        }
        const endpoint = `usterki/admin/${id}/`;
        const result = await updateData(endpoint, { status });
        setData(data.map(item => (item.id === id ? result : item)));
    };

    return (
        <div>
            <h1>Usterka</h1>
            <pre>{JSON.stringify(data, null, 2)}</pre>
            <Link to="/">Powrót do Home</Link>
            {!isAdmin && (
                <div>
                    <input
                        type="text"
                        value={newUsterka}
                        onChange={(e) => setNewUsterka(e.target.value)}
                        placeholder="Dodaj nową usterkę"
                    />
                    <button onClick={handleAddUsterka}>Dodaj</button>
                </div>
            )}
            {isAdmin && (
                <div>
                    {data.map((item) => (
                        <div key={item.id}>
                            <p>{item.opis}</p>
                            <select
                                value={item.status}
                                onChange={(e) => handleUpdateUsterka(item.id, e.target.value)}
                            >
                                <option value="nowa">Nowa</option>
                                <option value="w trakcie">W trakcie</option>
                                <option value="naprawiona">Naprawiona</option>
                            </select>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Usterka;
