import React, { useEffect, useState } from 'react';
import { fetchData, postData, updateData, deleteData } from '../api';
import { Link, useParams } from 'react-router-dom';
import './Common.css'; // Import the common CSS file

const Licznik = ({ isAdmin }) => {
    const { id } = useParams(); // Dla edycji/wyświetlania szczegółów licznika
    const [data, setData] = useState([]);
    const [newLicznik, setNewLicznik] = useState('');
    const [licznik, setLicznik] = useState(null);
    const [newStatus, setNewStatus] = useState('');

    useEffect(() => {
        const getData = async () => {
            if (id) {
                const result = await fetchData(`liczniki/${id}/`);
                setLicznik(result);
                setNewStatus(result.status); // Zakładając, że licznik ma status
            } else {
                const result = await fetchData('liczniki/');
                setData(result);
            }
        };
        getData();
    }, [id, isAdmin]);

    const handleAddLicznik = async () => {
        if (isAdmin) {
            alert('Admin nie może dodać nowego licznika');
            return;
        }
        try {
            console.log('Dodawanie licznika:', { opis: newLicznik });
            const result = await postData('liczniki/', { opis: newLicznik });
            setData([...data, result]);
            setNewLicznik('');
        } catch (error) {
            console.error('Błąd podczas dodawania licznika:', error);
            alert('Nie udało się dodać licznika');
        }
    };

    const handleUpdateLicznik = async () => {
        if (!isAdmin) {
            alert('Tylko admin może zmieniać status licznika');
            return;
        }
        try {
            const result = await updateData(`liczniki/admin/${id}/`, { status: newStatus });
            setLicznik(result);
            alert('Status licznika został zaktualizowany');
        } catch (error) {
            console.error('Błąd podczas aktualizowania licznika:', error);
            alert('Nie udało się zaktualizować licznika');
        }
    };

    const handleDeleteLicznik = async (id) => {
        try {
            await deleteData(`liczniki/${id}/`);
            setData(data.filter(item => item.id !== id)); // Usuwanie z widoku
            alert('Licznik został usunięty');
        } catch (error) {
            console.error('Błąd podczas usuwania licznika:', error);
            alert('Nie udało się usunąć licznika');
        }
    };

    const handleStatusChange = (id, status) => {
        setData(data.map(item => item.id === id ? { ...item, status } : item));
    };

    const handleUpdateStatus = async (id, status) => {
        try {
            const result = await updateData(`liczniki/admin/${id}/`, { status });
            setData(data.map(item => (item.id === id ? result : item)));
            alert('Status licznika zaktualizowany');
        } catch (error) {
            console.error('Błąd podczas zmiany statusu:', error);
            alert('Nie udało się zmienić statusu licznika');
        }
    };

    if (id && licznik) {
        return (
            <div className="container">
                <h1>Liczniki</h1>
                <p><strong>Opis:</strong> {licznik.opis}</p>
                <p><strong>Status:</strong> {licznik.status}</p>
                <select
                    value={newStatus}
                    onChange={(e) => setNewStatus(e.target.value)}
                >
                    <option value="nowa">Nowa</option>
                    <option value="w trakcie">W trakcie</option>
                    <option value="naprawiona">Naprawiona</option>
                </select>
                <button onClick={handleUpdateLicznik} className='btn btn-success'>Zaktualizuj status</button>
                
                {isAdmin && (
                    <button onClick={() => handleDeleteLicznik(licznik.id)} className='btn btn-danger'>Usuń licznik</button>
                )}

                <Link to="/" className="button">Powrót do Home</Link>
            </div>
        );
    }

    return (
        <div className="container">
            <h1>Liczniki</h1>
            {data.map((item) => (
                <div key={item.id} className="licznik-block">
                    <p><strong>ID:</strong> {item.id}</p>
                    <p><strong>Opis:</strong> {item.opis}</p>
                    <p><strong>Status:</strong> {item.status}</p>
                    {isAdmin && (
                        <div>
                            <select
                                value={item.status}
                                onChange={(e) => handleStatusChange(item.id, e.target.value)}
                            >
                                <option value="nowa">Nowa</option>
                                <option value="w trakcie">W trakcie</option>
                                <option value="naprawiona">Naprawiona</option>
                            </select>
                            <button onClick={() => handleUpdateStatus(item.id, item.status)} className='btn btn-success button-spacing2'>Zaktualizuj status</button>
                            <button onClick={() => handleDeleteLicznik(item.id)} className='btn btn-danger'>Usuń licznik</button>
                        </div>
                    )}
                </div>
            ))}
            <Link to="/" className="button">Powrót do Home</Link>
            {!isAdmin && (
                <div>
                    <input
                        type="text"
                        value={newLicznik}
                        onChange={(e) => setNewLicznik(e.target.value)}
                        placeholder="Dodaj nowy licznik"
                    />
                    <button onClick={handleAddLicznik}>Dodaj licznik</button>
                </div>
            )}
        </div>
    );
};

export default Licznik;
