import React, { useEffect, useState } from 'react';
import { fetchData, postData, updateData } from '../api';
import { Link, useParams } from 'react-router-dom'; // Importujemy useParams

const RozliczenieForm = () => {
    const { id } = useParams(); // Pobieramy id z parametrów URL
    const [data, setData] = useState([]);
    const [editRozliczenie, setEditRozliczenie] = useState(null); 
    const [newRozliczenie, setNewRozliczenie] = useState({
        kwota: '',
        data_rozliczenia: '',
        opis: '',
        status: 'oczekujące',
        mieszkaniec: '',
    });
    const [users, setUsers] = useState([]);

    useEffect(() => {
        const getData = async () => {
            const endpoint = 'rozliczenia/';
            const result = await fetchData(endpoint);
            setData(result);
        };

        const getUsers = async () => {
            const usersEndpoint = 'users/';
            const result = await fetchData(usersEndpoint);
            setUsers(result);
        };

        getData();
        getUsers();

        if (id) { // Jeżeli id jest w URL, oznacza to, że edytujemy rozliczenie
            const endpoint = `rozliczenia/${id}/`;
            fetchData(endpoint)
                .then((result) => {
                    setEditRozliczenie(result);
                    setNewRozliczenie({
                        kwota: result.kwota,
                        data_rozliczenia: result.data_rozliczenia,
                        opis: result.opis,
                        status: result.status,
                        mieszkaniec: result.mieszkaniec.id,
                    });
                })
                .catch((error) => {
                    console.error("Błąd pobierania rozliczenia:", error);
                });
        }
    }, [id]); // Zmienna zależna od id

    const handleAddOrUpdate = async () => {
        if (editRozliczenie) {
            // Edycja rozliczenia
            const endpoint = `rozliczenia/${editRozliczenie.id}/`;
            await updateData(endpoint, newRozliczenie);
            setEditRozliczenie(null);
        } else {
            // Dodawanie nowego rozliczenia
            const endpoint = 'rozliczenia/';
            await postData(endpoint, newRozliczenie);
        }
        setNewRozliczenie({
            kwota: '',
            data_rozliczenia: '',
            opis: '',
            status: 'oczekujące',
            mieszkaniec: '',
        });
    };

    return (
        <div className="container">
            <h1>{editRozliczenie ? 'Edytuj Rozliczenie' : 'Dodaj Nowe Rozliczenie'}</h1>
            <form>
                <input
                    type="number"
                    placeholder="Kwota"
                    value={newRozliczenie.kwota}
                    onChange={(e) => setNewRozliczenie({ ...newRozliczenie, kwota: e.target.value })}
                />
                <input
                    type="date"
                    value={newRozliczenie.data_rozliczenia}
                    onChange={(e) => setNewRozliczenie({ ...newRozliczenie, data_rozliczenia: e.target.value })}
                />
                <textarea
                    placeholder="Opis"
                    value={newRozliczenie.opis}
                    onChange={(e) => setNewRozliczenie({ ...newRozliczenie, opis: e.target.value })}
                />
                <select
                    value={newRozliczenie.status}
                    onChange={(e) => setNewRozliczenie({ ...newRozliczenie, status: e.target.value })}
                >
                    <option value="oczekujące">Oczekujące</option>
                    <option value="zrealizowane">Zrealizowane</option>
                    <option value="anulowane">Anulowane</option>
                </select>
                <select
                    value={newRozliczenie.mieszkaniec}
                    onChange={(e) => setNewRozliczenie({ ...newRozliczenie, mieszkaniec: e.target.value })}
                >
                    <option value="">Wybierz mieszkańca</option>
                    {users.map((user) => (
                        <option key={user.id} value={user.id}>
                            {user.first_name} {user.last_name}
                        </option>
                    ))}
                </select>
                <button type="button" onClick={handleAddOrUpdate}>
                    {editRozliczenie ? 'Zaktualizuj Rozliczenie' : 'Dodaj Rozliczenie'}
                </button>
            </form>
            <Link to="/rozliczenia">Powrót do listy rozliczeń</Link>
        </div>
    );
};

export default RozliczenieForm;
