import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link, Outlet } from 'react-router-dom';
import { login } from './api';
import Home from './components/Home';
import Mieszkaniec from './components/Mieszkaniec';
import Uchwala from './components/Uchwala';
import Harmonogram from './components/Harmonogram';
import Usterka from './components/Usterka';
import Login from './components/Login';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        const loginResult = await login(username, password);
        if (loginResult.token) {
            setIsLoggedIn(true);
        } else {
            alert('Login failed');
        }
    };

    return (
        <Router>
            <div>
                {!isLoggedIn ? (
                    <Login
                        username={username}
                        setUsername={setUsername}
                        password={password}
                        setPassword={setPassword}
                        handleLogin={handleLogin}
                    />
                ) : (
                    <>
                        <nav>
                            <ul>
                                <li><Link to="/">Home</Link></li>
                                <li><Link to="/mieszkaniec">Mieszkaniec</Link></li>
                                <li><Link to="/uchwala">Uchwala</Link></li>
                                <li><Link to="/harmonogram">Harmonogram</Link></li>
                                <li><Link to="/usterka">Usterka</Link></li>
                            </ul>
                        </nav>
                        <Routes>
                            <Route path="/" element={<Home />} />
                            <Route path="/mieszkaniec" element={<Mieszkaniec />} />
                            <Route path="/uchwala" element={<Uchwala />} />
                            <Route path="/harmonogram" element={<Harmonogram />} />
                            <Route path="/usterka" element={<Usterka />} />
                            <Route path="*" element={<Navigate to="/" />} />
                        </Routes>
                        <Outlet />
                    </>
                )}
            </div>
        </Router>
    );
};

export default App;
