import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import { login, getUserInfo } from './api';
import Home from './components/Home';
import Mieszkaniec from './components/Mieszkaniec';
import Uchwala from './components/Uchwala';
import Harmonogram from './components/Harmonogram';
import Usterka from './components/Usterka';
import Licznik from './components/Licznik';
import Rozliczenia from './components/Rozliczenia';
import Login from './components/Login';
import { AuthProvider } from './context/AuthContext'; // Importujemy AuthProvider
import DodajSpotkanie from './components/DodajSpotkanie';
import SpotkanieDetails from './components/SpotkanieDetails';

const App = () => {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [isAdmin, setIsAdmin] = useState(false);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleLogin = async (e) => {
        e.preventDefault();
        const loginResult = await login(username, password);
        if (loginResult.token) {
            setIsLoggedIn(true);
            const userInfo = await getUserInfo();
            setIsAdmin(userInfo.is_staff);
        } else {
            alert('Login failed');
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token'); // Usuwamy token z localStorage
        setIsLoggedIn(false);  // Ustawiamy stan na false
        setUser(null);  // Usuwamy dane użytkownika
    };

    useEffect(() => {
        const checkUserInfo = async () => {
            if (isLoggedIn) {
                const userInfo = await getUserInfo();
                setIsAdmin(userInfo.is_staff);
            }
        };
        checkUserInfo();
    }, [isLoggedIn]);

    useEffect(() => {
        const checkUserInfo = async () => {
            const token = localStorage.getItem('token');
            if (token) {
                const userInfo = await getUserInfo();
                if (userInfo) {
                    setIsLoggedIn(true);
                    setIsAdmin(userInfo.is_staff);
                } else {
                    localStorage.removeItem('token');
                }
            }
        };
        checkUserInfo();
    }, []);

    return (
        <AuthProvider>
            <Router>
                <div>
                    {!isLoggedIn ? (
                        <Login
                            username={username}
                            setUsername={setUsername}
                            password={password}
                            setPassword={setPassword}
                            handleLogin={handleLogin}
                        />
                        
                    ) : (
                        <>

                           {/* Pasek nawigacyjny */}
                    <header style={styles.header}>
                        <div>
                            <Link to="/" style={styles.link}>Home</Link>
                            <Link to="/harmonogram" style={styles.link}>Harmonogram</Link>
                            {/* Można dodać inne linki */}
                        </div>
                        <div style={styles.logoutSection}>
                            {isLoggedIn ? (
                                <>
                                    
                                    <button onClick={handleLogout} className="btn btn-danger" style={styles.logoutButton}>Wyloguj się</button>
                                </>
                            ) : (
                                <Link to="/login" className="btn btn-primary" style={styles.loginButton}>Zaloguj się</Link>
                            )}
                        </div>
                    </header>
                            <Routes>
                                <Route path="/" element={<Home />} />
                                <Route path="/mieszkaniec" element={<Mieszkaniec />} />
                                <Route path="/uchwala" element={<Uchwala />} />
                                <Route path="/harmonogram" element={<Harmonogram />} />
                                <Route path="/usterka" element={<Usterka isAdmin={isAdmin} />} />
                                <Route path="/usterki/admin/:id" element={<Usterka isAdmin={isAdmin} />} />
                                <Route path="/liczniki" element={<Licznik />} />
                                <Route path="/rozliczenia" element={<Rozliczenia />} />
                                <Route path="*" element={<Navigate to="/" />} />
                                <Route path="/dodaj-spotkanie" element={<DodajSpotkanie />} /> {/* Trasa do formularza */}
                                <Route path="/spotkanie/:id" element={<SpotkanieDetails />} /> {/* Trasa do szczegółów spotkania */}
                            </Routes>
                        </>
                    )}
                </div>
            </Router>
        </AuthProvider>
    );
};

const styles = {
    header: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '10px',
        backgroundColor: '#f1f1f1',
        boxShadow: '0px 4px 2px -2px gray',
    },
    link: {
        margin: '0 15px',
        textDecoration: 'none',
        color: 'black',
    },
    logoutSection: {
        display: 'flex',
        alignItems: 'center',
    },
    userName: {
        marginRight: '10px',
    },
    logoutButton: {
        marginLeft: '10px',
        padding: '5px 10px',
        cursor: 'pointer',
        backgroundColor: '#d9534f',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
    },
    loginButton: {
        padding: '5px 10px',
        cursor: 'pointer',
        backgroundColor: '#5bc0de',
        color: 'white',
        border: 'none',
        borderRadius: '4px',
    },
};

export default App;
